import React from "react";
import styled from "styled-components";
import { useParams } from "react-router-dom";

const Review = (props) => {
    const day = useParams();
    console.log(day)
    return (
        <div>
            <TopContainer>
                <Day>{day.day}요일</Day>
                평점 남기기
            </TopContainer>
            <CircleContainer>
                <GrayCircle />
                <GrayCircle />
                <GrayCircle />
                <GrayCircle />
                <GrayCircle />
            </CircleContainer>
            <Button>평점 남기기</Button>
        </div>
    );
}
const TopContainer = styled.div`
    text-align: center;
    font-weight: bold;
    font-size: 25px;
`;

const Day = styled.span`
    background-color: orange;
    color:white;
    margin: 0 10px 0 0;
    padding: 0 5px;
    border-radius: 5px;
`;

const Container = styled.div`
    text-align: center;
    display: flex;
    justify-content: center;
    align-items: center;
    margin: 15px;
`;
const CircleContainer = styled.div`
    text-align: center;
    display: flex;
    justify-content: center;
    align-items: center;
    margin: 15px;
`;

const YellowCircle = styled.div`
    width: 30px;
    height: 30px;
    border-radius: 30px;
    margin: 5px;
    background-color: yellow;
`;

const GrayCircle = styled.div`
    width: 30px;
    height: 30px;
    border-radius: 30px;
    margin: 5px;
    background-color: #eee;
`;

const Button = styled.button`
    width: 100%;
    background-color: purple;
    border: none;
    border-radius: 5px;
    padding: 1rem;
    color: rgb(255, 255, 255);
`;

export default Review
