import React from 'react';
import styled from 'styled-components'
import Score from './Score';


function App() {
  let day = {};
  let days = ['월','화','수','목','금','토','일'];
  for (let i = 0; i < 7; i++) {
    day[i] = Math.floor((Math.random()*10)/2)+1;
    console.log(day[i])
  }
  console.log('day : ', day);
  return (
    <Container>
      <Title>내 일주일은?</Title>
      {days.map((list, index) => {
        return (
          <Score 
            key = {index}
            day = {list}
          />
        );
      })};
    </Container>
  );
}

const Container = styled.div`
  max-width: 350px;
  width: 80vw;
  height: 80vh;
  margin: 5vw auto;
  padding: 5vh 0px;
  border: 1px solid rgb(221, 221, 221);
  box-sizing: border-box;
  border-radius: 5px;
`;

const Title = styled.div`
  text-align: center;
  font-size: 20px;
  font-weight: bold;
`;

export default App;
